import React,{useState} from 'react';
import './App.css';
import Card from './Card'
import axios from 'axios'



function App() {
  const [data,setData]=useState()
  axios.get('http://localhost:5000')
  .then(res => {
    console.log(res)
  const sqldata = res.data
  setData(JSON.stringify(sqldata)) 
})
function cardsRender(item)
  {
    return(
      <Card key ={item.FName} name={item.FName} address={item.Address}/>
    )
  } 
  return (
    <div className="App">
      {data.map(cardsRender)}
    </div>
  );
}

export default App;
