import React, { useState } from 'react'

function Bill(props){
    const [amt,setAmt]=useState(0)
    let tempAmt=[]
    const data=props.data

    function checkOutDb(val)
    {props.checkOutDb(val)
    props.setAlert(true)}

    function billRender(obj){
        tempAmt.push(obj.price)
        return(
        <p class="card-text">{obj.name} <br/> {obj.price}</p>
        )
    }
    return(
        <>
        <div class="card bill">
        <div class="card-header">
           Checkout
        </div>
        <div class="card-body">
            <h5 class="card-title">Bill</h5>
            {data.map(billRender)}
            <h3 class="card-text">{tempAmt.reduce((a, b) => a + b,0)}</h3>
            <a href="#" class="btn btn-primary">Go somewhere</a>
        </div>
        </div>
        </>
    )}

export default Bill
