import React from 'react'
function Card(props){
    
    function updateDb(){
        let q=`INSERT INTO CART (CartID, TotalCost)
        SELECT ProductID, Price FROM PRODUCT where ProductID = ${props.ProductID};
        INSERT INTO ADDEDTO (ProductID ,CartID)
        SELECT ProductID, ProductID FROM PRODUCT where ProductID = ${props.ProductID};`
        // let q=props.name
        props.updateDb(q)
    }
    return(
        <div class="card custom-card">
            <img class="card-img-top" src="..." />
            <div class="card-body">
                <h5 class="card-title">{props.name}</h5>
                <p class="card-text">{props.model}</p>
                <h5 class="card-title">{props.price}</h5>
                <a href="#" class="btn btn-primary" onClick={updateDb}>Add to Cart</a>
            </div>
        </div>
    )
}
export default Card
