import React, { useState } from 'react'

function Bill(props){
    const [amt,setAmt]=useState(0)
    data=props.data
    function billRender(obj){
        setAmt(amt+obj.price)
        return(
        <p class="card-text">{obj.name} <br/> {obj.price}</p>
        )
    }
    return(
        <>
        <div class="card">
        <div class="card-header">
           Checkout
        </div>
        <div class="card-body">
            <h5 class="card-title">Bill</h5>
            {data.map(billRender)}
            <h3 class="card-text">{amt}</h3>
            <a href="#" class="btn btn-primary">Go somewhere</a>
        </div>
        </div>
        </>
    )}

export default Bill
