import React,{useState, useEffect} from 'react';
import './App.css';
import Cards from './Cards'
import axios from 'axios'
import Nav from './Nav'

const que="INSERT INTO da VALUES('abc','a','b',123456,'01-JAN-20','abc','M',80000,12345,3)"
const [priceArr,setPriceArr]=useState([])

function App() {
  const [data,setData]=useState(null)
  useEffect(()=>{
    axios.get('http://localhost:5000')
    .then(res => {
    console.log(res.status)
    setData(res.data)})
  },[])

  function checkOutDb(q){
    axios.post('http://localhost:5000/post',{'query':q})
    .then(res=>{
      console.log(res)
    })
  }
  function updateDb(val){
    let tempArr=priceArr
    tempArr.push(val)
    setPriceArr(tempArr)
  }

  function checkDb(){
    axios.get('http://localhost:5000/cart')
    .then(res=>{
      console.log(res)
    })
  }

  return (
    <div className="App">
      <Nav />
      <br/><br/><br/><br/>      
      {(data!=null)?<Cards data={data} updateDb={updateDb}/>:<br/>}
      <h1>CART</h1>
      <Cards data={priceArr} />
      <button onClick={checkDb}>CART</button>

    </div>
  )
}

export default App;
